import React, { Component } from 'react';
import {
  Platform,
  SegmentedControlIOS,
  KeyboardAvoidingView,
  View
} from 'react-native';

import styles from 'styles';

export default class InputContainer extends Component {
  constructor(props){
    super(props);
    this.state = {
      behavior: 'position',
    };
  }

  renderForIOS = () => {
    return (
      <KeyboardAvoidingView behavior={this.state.behavior} style={this.props.style}>
        {this.props.children}
      </KeyboardAvoidingView>
    )
  }

  renderForAndroid = () => {
    return (
      <View style={this.props.style}>
        {this.props.children}
      </View>
    )
  }

  render(){
    switch(Platform.OS.toLowerCase()){
      case 'ios': {
        return this.renderForIOS()
      }
      case 'android': {
        return this.renderForAndroid() 
      }
    }
  }
}