import Types from 'constants/types';
const initialState = {
  view: undefined,
  bindingDone: false
}

module.exports = (state=initialState, action) => {
  switch(action.type){
    case Types.SET_HOME_VIEW: {
      return Object.assign(
        {}, state, { view: action.payload }
      );
    }

    case Types.SET_HOME_BINDING: {
      return Object.assign(
        {}, state, { bindingDone: action.payload }
      );
    }

    default:
      return state;
  }
}