import Types from 'constants/types';
import drawerConstants from 'constants/drawer'
const initialState = {
  index: 0,
  title: 'Connect'
}

module.exports = (state=initialState, action) => {
  switch(action.type){
    case Types.SET_MAIN_INDEX: {
      console.log(drawerConstants[action.payload])
      return Object.assign(
        {}, state, {
          index: action.payload,
          title: drawerConstants[action.payload].name
        }
      );
    }

    default:
      return state;
  }
}