import { Platform } from 'react-native'

const UtilityMethods = {
  applyLetterSpacing: function(text, count){
    if (Platform.OS.toLowerCase() == 'android'){
      if (text == undefined || text == '') return ''; 
      return text.split('').join('\u200A'.repeat(count));
    }else{
      return text;
    }
  },
  drawerType: Platform.OS.toLowerCase() === "ios" ? "static" : "overlay",
  mp3URL: Platform.OS.toLowerCase() === "ios" ? "http://www.headerhandler.com/downloads/test.mp3" : "test"
}

module.exports = UtilityMethods;